export const Productadd = () => {
    const dispatch = useDispatch();
    const {q,id} = useParams();
    const [data, setData] = React.useState(null);
    //data will be only one
    //then add to cart
    React.useEffect(() => {
        const config = {
            method: 'get',
            url:  `https://meesho-all.herokuapp.com/${q}?id=${id}`
           
        }
        axios(config)
            .then((res) => {
                console.log(res.data)
                setData(res.data);
            })
    },[q],[id])
    return (
        data ? (
            // add to card data
            <Productcard handleClick={() => dispatch(addToCart(data[0]))} key={data[0]?.id} title={data[0]?.title} discountAmt={data[0]?.d_price} Amt={data[0]?.price} url={data[0]?.img} rate={data[0]?.rating} d1={data[0]?.details.fabric} d2={data[0]?.details.pattern} d3={data[0]?.details.pattern} d4={data[0]?.details.pattern} seller={data[0]?.seller} />
        ) : (<div></div>)
    )
}
